// Ensure chatInput field is properly set and add smart search optimization
const input = $input.first().json;
console.log('Input received:', JSON.stringify(input, null, 2));

// Extract message text from various possible fields
const messageText = input.text || input.message || input.chat_message || input.prompt || input.input || input.chatInput || input.query || '';
console.log('Extracted message:', messageText);

// Smart search optimization based on query type
let scoreThreshold = 0.3;
let topK = 5;

// Adjust search parameters based on query content
const queryLower = messageText.toLowerCase();

// For very specific terms, use even lower threshold
if (queryLower.includes('alliance') || queryLower.includes('premiere suites')) {
  scoreThreshold = 0.2;
}
// For specific questions, use higher threshold for better precision
else if (queryLower.includes('what is') || queryLower.includes('how do') || queryLower.includes('do you')) {
  scoreThreshold = 0.4;
}
// For general questions, use lower threshold for broader results
else if (queryLower.includes('tell me') || queryLower.includes('explain') || queryLower.includes('about')) {
  scoreThreshold = 0.25;
}

console.log(`Search params: threshold=${scoreThreshold}, topK=${topK}`);

return {
  json: {
    ...input,
    chatInput: messageText, // This is what the agent expects
    text: messageText,
    message: messageText,
    query: messageText, // Also set query field for compatibility
    scoreThreshold: scoreThreshold,
    topK: topK,
    debug_search: `Query: "${messageText}", Threshold: ${scoreThreshold}, TopK: ${topK}`
  }
};
